INSERT INTO `products` VALUES (1,'芬兰美卓METSO-ND9000系列智能定位器','ND9106HXT','<h5><span style=\"color: rgb(51, 51, 51);\">METSO&nbsp;美卓 定位器&nbsp;ND9103HNT</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓ND9102HX1</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓ND9102HX2-102</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓ND9103HX1</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓ND9106HX1</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">美卓&nbsp;5H36HBRT</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">美卓&nbsp;METSO ND9106HXT</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">美卓&nbsp;METSO&nbsp;定位器&nbsp;ND9106HNT/K05</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓&nbsp;EN33A05DM</span></h5><h5><span style=\"color: rgb(51, 51, 51);\">metso美卓&nbsp;ND91006HXT</span></h5>','http://www.hbjomay.com/uploads/images/product-docs/201806/11//_1528721230_BYLuFTXxv9.pdf','http://www.hbjomay.com/uploads/images/products/201806/11//_1528721230_R5r7ih3fD2.jpg',44,1,'2018-06-11 12:47:10','2018-06-11 12:47:10'),(2,'美卓METSO-NP700 系列气动定位器','NP','<p><b>技术规范</b></p><p>标准输入信号4 - 20毫安，0 - 20毫安（直流）</p><p>分裂范围4 - 12毫安，12 - 20毫安</p><p>输入电阻最大190Ω</p><p>转向角</p><p>反馈轴最大95°</p><p>转角关系</p><p>信号线性</p><p>供给压力P S 0.14～0.8 MPa</p><p>1.4 - 8巴格/ 20 - 115磅/英寸</p><p>供气压力P＜0.2%／10 kPa／0.14%／psi的影响</p><p>环境温度- 25至85°C//至15°至185°F</p><p>温度＜0.05%°C／＜0.025%°/°F的影响</p><p>中等恒力执行器的性能</p><p>-死区&lt; 0.3%</p><p>-滞后＜0.7%</p><p>-线性＜2%</p><p>对振动的影响（1.5克，5-100赫兹）＜1%</p><p><br></p><p><b>材料：</b></p><p>铝阳极氧化膜，环氧树脂涂层。</p><p>-覆盖层为聚碳酸酯或铝合金。内部零件是</p><p>不锈钢和铝合金。</p><p>-膜片和密封件是丁腈橡胶（标准模型）。</p><p>重量约2.3公斤/磅4.8磅</p><p>标准信号</p><p>压力范围20～100千帕</p><p>0.2 - 1巴格/ 3 - 15磅/英寸</p><p>分裂范围20 - 60千帕，60 - 100千帕</p><p>3 - 9磅/秒，9—15磅/英寸</p><p>转向角</p><p>反馈轴最大95°</p><p>转角关系</p><p>信号线性</p><p>供给压力P S 0.14～1 MPa</p><p>1.4 - 10巴格/ 20 - 140磅/英寸</p><p>供给压力的影响＜0.2%／10 kPa／0.14%／psi</p><p>环境温度- 40至90°C//至40°至200°F</p><p>温度＜0.07%°C／＜0.025%°/°F的影响</p><p>＜0.15%°C／＜0.05%°/°F</p>','h